	[1013041] = L"Help! Guards! Flood, fire, famine!",
	[1013042] = L"Aaaah! They will kill me! Guards!",
	[1013043] = L"Arrest this scum!",
	[1013044] = L"Look! Tis that evil one! Guards!",
	[1013045] = L"Beware! 'Tis that scoundrel! Guards!",
	[1013046] = L"Look thee, a criminal! Guards!",
	[1013047] = L"Thy like is not welcome here! Guards! arrest this person!",
	[1013048] = L"Scum like thee is not welcome here. Guards!",
	[1013049] = L"To think I saw thee and lived! Guards!",
	[1013050] = L"We tolerate not those like thee! Guards!",
	[1013051] = L"Get out of here, scum! Guards!",
	[1013052] = L"Guards! Guards!",
