	[1113948] = L"CITY NAMES:<br>britain, bucs, cove, delucia, haven, jhelom, magincia, minoc, moonglow, nujelm, ocllo, papua, serpent, skara, trinsic, vesper, wind, yew, luna, umbra, zento, termur, ilshenar",
	[1113949] = L"MOONGATE NAMES<br>moonglow, britain, jhelom, yew, minoc, trinsic, skara, magincia, haven, bucs, vesper, compassion, honesty, honor, humility, justice, sacrifice, spirituality, valor, chaos, ",
	[1113950] = L"luna, umbra, isamu, makoto, homare, termur<br><br><br>The same teleportation rules apply regarding criminal flagging, weight, etc.",
