	[1114134] = L"to the northwest",
	[1114135] = L"in some direction",
	[1114137] = L"Ar! Did I ever tell thee about the time I was in ~1_CITY~ and I met ~2_GENDER~ ~3_STORY~ Anyway, 'tis a dull story.",
	[1114138] = L"Yew",
	[1114139] = L"Minoc",
	[1114140] = L"Vesper",
	[1114141] = L"New Magincia",
	[1114142] = L"Trinsic",
