	[1060166] = L"You feel disoriented!",
	[1060167] = L"The bleeding wounds have healed, you are no longer bleeding!",
	[1060168] = L"Your confusion has passed, you may now arm a weapon!",
	[1060169] = L"You cannot use this ability while dead.",
	[1060170] = L"You cannot use this ability while frozen.",
	[1060171] = L"You cannot use this ability while stunned.",
	[1060172] = L"You must have at least ~1_SKILL_REQUIREMENT~ Chivalry to use this ability,",
	[1060173] = L"You must have at least ~1_TITHE_REQUIREMENT~ Tithing Points to use this ability,",
	[1060174] = L"You must have at least ~1_MANA_REQUIREMENT~ Mana to use this ability.",
	[1060175] = L"You cannot cleanse a creature of poison when it is already dead!",
	[1060176] = L"That creature is not poisoned!",
