	[1153100] = L"Add Charges",
	[1024512] = L"floor spikes",
	[1062164] = L"Door <a href = \"?146\">components</a> are one type of <a href = \"?156\">fixture</a> you can add to your house.  Simply choose the style of door from the <a href = \"?157\">component menu</a> you wish to paint on your house design.  Like <a href = \"?173\">walls</a>, doors require<a href = \"?163\"> structural support</a>, and can provide support for items above them.",
	[1024513] = L"floor spikes",
