	[1075673] = L"There are no ~1_targettype~ on the screen to select.",
	[1029357] = L"wooden wall",
	[1075675] = L"Greetings my friend! My name is Gareth, and I represent a group of citizens who wish to rejuvenate interest in our kingdom's noble heritage. 'Tis our belief that one of Britannia's greatest triumphs was the institution of the Virtues, neglected though they be now. To that end I have a set of tasks prepared for one who would follow a truly Humble path. Art thou interested in joining our effort?",
	[1029358] = L"wooden wall",
	[1075677] = L"I wish that thou wouldest reconsider.",
	[1029359] = L"wooden wall",
	[1075679] = L"A Shepherd\x2019s Crook",
	[1029360] = L"wooden wall",
	[1075681] = L"A Bowed Head ",
	[1029361] = L"wooden wall",
	[1075683] = L"<center>What opposes Humility?</center>",
	[1029362] = L"wooden wall",
	[1075685] = L"Pride ",
	[1029363] = L"wooden wall",
