	[1029522] = L"window shutter",
	[1076005] = L"Welcome to the home of ~1_OWNER_NAME~. Please keep your shoes off the furniture.",
	[1029523] = L"window shutter",
	[1076007] = L"My ~1_OWNER_SEX~, you have a visitor! ~2_VISITOR_TITLE~ ~3_VISITOR_NAME~ is awaiting your presence.",
	[1029524] = L"window shutter",
	[1076009] = L"Ahh, greetings to ~1_VISITOR_TITLE~ ~2_VISITOR_NAME~. Your coming here was foreseen, and I alone know of your purpose.",
