	[1029541] = L"jungle wall",
	[1076043] = L"*squints* Not you again! Fine, fine... whatever... come on in, I suppose. *sighs*",
	[1029542] = L"jungle wall",
	[1076045] = L"Make way ye unwashed hordes! Clear the road! For ~1_TITLE~ ~2_NAME~ has business more important than yours!",
