	[501988] = L"You smelt the ore removing the impurities and put the metal in your backpack.",
	[1076050] = L"Sorry, we are unable to get to the destination.",
	[1076048] = L"~1_TITLE~ ~2_NAME~ has arrived! Let the drinks flow! Let the festivities commence! And you there, with the pig, get that beast on a skewer!",
	[1076046] = L"~1_TITLE~ ~2_NAME~ approaches! Be ye prepared to kneel before their indomitable presence! And remember, tribute is to be only in gold!",
	[501989] = L"You burn away the impurities but are left with no useable metal.",
	[1076044] = L"Attention, attention! All hail the arrival of the ~1_TITLE~ ~2_NAME~!",
	[1076042] = L"Ahh, greetings to ~1_VISITOR_TITLE~ ~2_VISITOR_NAME~. Your coming here was foreseen, and I alone know of your purpose.",
	[1076040] = L"My ~1_OWNER_SEX~, you have a visitor! ~2_VISITOR_TITLE~ ~3_VISITOR_NAME~ is awaiting your presence.",
	[501990] = L"You burn away the impurities but are left with less useable metal.",
