	[1076014] = L"Throw down your capes and kerchiefs! Let the petals be strewn! For the ~1_TITLE~ ~2_NAME~ approacheth!",
	[1076012] = L"Make way ye unwashed hordes! Clear the road! For ~1_TITLE~ ~2_NAME~ has business more important than yours!",
	[1076010] = L"*squints* Not you again! Fine, fine... whatever... come on in, I suppose. *sighs*",
